INSERT INTO drugs (name, atc_chemical_substance_id, quantity, description) VALUES ('LIDOCAINE HYDROCHLORIDE', (SELECT id FROM atc_chemical_substance WHERE atc_code='N01BB02'), 14, 'Transparent colorless or slightly yellowish solution.');
